import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import React from 'react'

const ImageSlider = ({images}) => {

const settings = {
	infinite: true,
	dots: true,
	fade:true,
	slidesToShow: 1,
	slidesToScroll: 1,
	adaptiveHeight:true,
	lazyLoad: true,
	autoplay: true,
autoplaySpeed: 2000,

};
return (
	<>
	<div className="tag">
		<h1 className="text-center font-bold text-xl">My Digital Art</h1>
	</div>
	<div className="imgslider text-center">
		<Slider {...settings}>
		{images.map((item) => (
			<div key={item.id}>
			<img src={item.src} alt={item.alt} className="text-center h-{}" />
			</div>
		))}
		</Slider>
	</div>
		</>
)
}
export default ImageSlider;
