import React from 'react'
import blogs from "./blog.json";
import Navbar from './navbar';
export default function Blog() {
  return (
    <div className="bg-[#8e41b0] h-screen">
      <Navbar/>
        <h1 className='text-3xl text-white font-semibold pt-10 text-center '>My Blogs</h1>
        <div className='flex flex-col items-center justify-center gap-10 mt-10'>
        {blogs.map((blog)=>{
          return(

            <div className='bg-white w-[40vw] p-4 rounded-lg border-2 border-black'>
              <div className='text-white p-2' style={{backgroundImage:`url("https://tenor.com/view/comic-yes-gif-18440126")`}}>
              <p className='text-sm'>{blog.date}</p>
              <h1 className='text-xl m-2'>{blog.title}</h1>
              <div className='flex justify-between m-2'>
              <p className='text-sm'>{blog.author}</p>
              <button>Read Me</button>
              </div>
            </div>
            </div>
            
          )
        })}
        </div>
    </div>
  )
}
